import Dispatcher from 'flux';

class CountdownDispatcher extends Dispatcher.Dispatcher {

    handleAction(action) {
        console.log('dispatching action:', action)
        this.dispatch({
            source: 'VIEW_ACTION',
            action,
        })
    }

}

export default CountdownDispatcher;
